function Post({
  post,
  post: { id, titulo, img, descripcion, likes },
  like,
  eliminarPost
}) {
  return (
    <div className="card col-12 col-sm-4 d-inline mx-0 px-3">
      <div className="card-body  p-0">
        <img className="card-img-top " src={img} />
        <div className="p-3">
          <h4 className="card-title">{titulo}</h4>
          <p className="card-text">{descripcion}</p>
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <button
                onClick={() => like(id)}
                className={`fa-heart fa-xl ${likes ? "fa-solid" : "fa-regular"
                  }`}
              >Like</button>
              <span className="ms-1">{likes}</span>
            </div>
            <button onClick={() => eliminarPost(id)} className="fa-solid fa-x">Eliminar</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Post;
